import React, { useState } from 'react';
import EarthModel from '../components/3d/EarthModel';
import EnergyChart from '../components/charts/EnergyChart';
import { predictEnergy } from '../services/api';
import { Input, Button } from '../components/ui';

const BipvToolPage = () => {
    const [buildingArea, setBuildingArea] = useState('');
    const [orientation, setOrientation] = useState('N');
    const [panelType, setPanelType] = useState('Standard');
    const [predictionResult, setPredictionResult] = useState(null);

    const handlePrediction = async () => {
        const result = await predictEnergy({
            area_sq_ft: buildingArea,
            orientation,
            panel_type: panelType,
        });
        setPredictionResult(result);
    };

    return (
        <div>
            <h1>BIPV Management & Prediction Tool</h1>
            <EarthModel />
            <div>
                <h2>Building Information</h2>
                <Input
                    type="number"
                    placeholder="Building Area (sq ft)"
                    value={buildingArea}
                    onChange={(e) => setBuildingArea(e.target.value)}
                />
                <select value={orientation} onChange={(e) => setOrientation(e.target.value)}>
                    <option value="N">North</option>
                    <option value="S">South</option>
                    <option value="E">East</option>
                    <option value="W">West</option>
                </select>
                <select value={panelType} onChange={(e) => setPanelType(e.target.value)}>
                    <option value="Standard">Standard</option>
                    <option value="High Efficiency">High Efficiency</option>
                </select>
                <Button onClick={handlePrediction}>Predict Energy Generation</Button>
            </div>
            {predictionResult && (
                <div>
                    <h2>Prediction Results</h2>
                    <EnergyChart data={predictionResult} />
                    <p>Predicted Energy Generation: {predictionResult.energy_output_kwh} kWh</p>
                    <p>Carbon Reduction: {predictionResult.carbon_reduction_tons} tons</p>
                    <p>Estimated Savings: ${predictionResult.estimated_savings}</p>
                </div>
            )}
        </div>
    );
};

export default BipvToolPage;
