import React from 'react';
import { Link } from 'react-router-dom';
import Header from '../components/layout/Header';
import Footer from '../components/layout/Footer';

const HomePage: React.FC = () => {
    return (
        <div>
            <Header />
            <main className="flex flex-col items-center justify-center min-h-screen bg-gray-100">
                <h1 className="text-4xl font-bold mb-4">Welcome to the Green IT & Solar BIPV Management System</h1>
                <p className="text-lg mb-6">
                    Empowering users to manage energy consumption, predict solar energy generation, and understand their impact on carbon credits.
                </p>
                <Link to="/bipv-tool" className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600">
                    Get Started
                </Link>
                <section className="mt-10">
                    <h2 className="text-2xl font-semibold">Key Features</h2>
                    <ul className="list-disc list-inside mt-4">
                        <li>Interactive 3D Earth Model for location-based energy prediction</li>
                        <li>Comprehensive Energy Management Dashboard</li>
                        <li>Carbon Credit Calculator to track your savings</li>
                        <li>Educational Resources on Green IT and sustainable practices</li>
                    </ul>
                </section>
            </main>
            <Footer />
        </div>
    );
};

export default HomePage;
