import React from 'react';
import { Link } from 'react-router-dom';
import EarthModel from '../components/3d/EarthModel';
import Button from '../components/ui/Button';

const HomePage = () => {
  const containerStyle: React.CSSProperties = {
    textAlign: 'center',
    padding: '50px 20px',
  };

  const headlineStyle: React.CSSProperties = {
    fontSize: '2.5rem',
    color: '#333',
  };

  const missionStyle: React.CSSProperties = {
    fontSize: '1.2rem',
    color: '#666',
    maxWidth: '600px',
    margin: '0 auto 30px auto',
  };

  return (
    <div style={containerStyle}>
      <h1 style={headlineStyle}>Welcome to SolarSense</h1>
      <p style={missionStyle}>
        Empowering you to manage energy consumption, predict solar BIPV energy generation, and understand your carbon footprint.
      </p>

      {/* This is where the 3D model will be rendered */}
      <EarthModel />

      <div style={{ marginTop: '30px' }}>
        <Link to="/bipv-tool">
          <Button>Get Started</Button>
        </Link>
      </div>
    </div>
  );
};

export default HomePage;
